package main

import (
	"log"

	"github.com/nikirill/go-crypto/openpgp"
	"github.com/si-co/vpir-code/lib/pgp"
)

func main() {
	//key, err := hex.DecodeString("")
	//r := bytes.NewReader(key)
	var m map[string]*openpgp.Entity
	var err error
	m, err = pgp.AnalyzeDumpFiles()
	if err != nil {
		log.Fatal(err)
	}
	err = pgp.WriteKeysOnDisk(m)
	if err != nil {
		log.Fatal(err)
	}
}
